  const a = 4
  const b = 4

  return(
    <div className = "App">
      <h1>Pagina con React</h1>
      <Mensaje message='Estoy haciendo chocolate casero'/>
      <Mensaje message='Tenia tantas ganas de comerme un chocolate'></Mensaje>
      <Mensaje message='Solo si lo veo'></Mensaje>
      <Descripcion/>
